/**
 * Created by AneeqShah on 2/26/19.
 */


import React from 'react';
import {API_URL} from '../../config';
import {handleResponse, renderChange} from '../../helpers';
import Loading from '../common/Loading';

import './Detail.css';


class Detail extends React.Component{


    constructor(){

        super();

        this.state ={

            currency: {},
            loading: false,
            error: null,

        };
    }

    componentDidMount(){

        const currencyId = this.props.match.params.id;

        this.setState({loading: true});

        fetch(`${API_URL}/cryptocurrencies/${currencyId}`)
            .then(handleResponse)
            .then((currency) => {

                this.setState({
                    loading:false,
                    error: null,
                    currency,

                });

            })
            .catch((error) => {
                this.setState({
                    error: error.errorMessage,
                    loading: false,
                });
            });

    }

    render(){

        const {loading, error, currency} = this.state;

        //render when loading data from API
        if(loading){
            return <div className="loading-container"><Loading/></div>;
        }


        //render when error getting data from API
        if(error){
            return <div className="error">{error}</div>
        }

        return(
            <div className="Detail">
                <h1 className="Detail-heading">
                    {currency.name} ({currency.symbol})
                </h1>

                <div className="Detail-container">
                    <div className="Detail-item">
                        Price: <span className="Detail-value">$ {currency.price}</span>
                    </div>
                    <div className="Detail-item">
                        Rank: <span className="Detail-value">{currency.rank}</span>
                    </div>
                    <div className="Detail-item">
                        24H Change: <span className="Detail-value">{renderChange(currency.percentChange24h)}</span>
                    </div>
                    <div className="Detail-item">
                        <span className="Detail-item">Market Cap:</span>
                        <span className="Detail-dollar">$</span>
                    </div>
                </div>
            </div>

        );
    }
}

export default Detail;
